  name: string;
  node: XmlElement | XmlText;
  attributes: Record<string, string>;
  children?: MMLTag[];
  key?: string;

  constructor(name: string, node: XmlElement | XmlText, children?: MMLTag[]) {
    this.name = name;
    this.node = node;
    this.attributes = (this.node as XmlElement).attributes || {};
    this.children = children;
  }

