import React, { useState, useRef, useCallback, useEffect, FC } from 'react';
import { Virtuoso } from 'react-virtuoso';
import { Dayjs } from 'dayjs';
import { DatePickerProps } from './DatePicker';

export type DatePickerSelectProps = DatePickerSelectReadyProps & {
  itemClassName?: string;
  getItemData: (props: DatePickerSelectItemProps) => DatePickerItemData;
};

/**
 * Basic shape of DatePickerSelect extended by wrapper components as DatePickerDate and DatePickerTime
 */
export type DatePickerSelectReadyProps = Pick<DatePickerProps, 'icalFilter'> & {
  format: string;
  interval: number;
  onChange: (value: Dayjs) => void;
  value: Dayjs;
};

/**
 * Inside Virtuoso select items need an idx to indicate their position
 */
export type DatePickerSelectItemProps = {
  format: string;
  idx: number;
  interval: number;
  value: Dayjs;
};

/**
 * The data needed by each datepicker select item
 */
export type DatePickerItemData = {
  displayValue: string;
  idx: number;
  isSelected: (currentValue: Dayjs) => boolean;
  value: Dayjs;
};

const ITEMS_PER_PAGE = 40;
const VERTICAL_COMPENSATION = 3;
const INITIAL_INDEX = ITEMS_PER_PAGE;
const VIRTUOSO_START_INDEX = 10000;

/**
 * DatePicker select
 */
export const DatePickerSelect: FC<DatePickerSelectProps> = (props) => {
  const { onChange, icalFilter, getItemData, itemClassName, interval, format, value } = props;

  // Generate date items
  const generateItems = useCallback(
    (quantity: number, firstIdx: number) => {
      return Array(quantity)
        .fill(true)
        .map((_, idx) => getItemData({ interval, format, value, idx: firstIdx + idx }))
        .filter((newItem) => !icalFilter || (icalFilter && icalFilter(newItem.value)));
    },
    [interval, format, value, icalFilter, getItemData],
  );

  const [items, setItems] = useState<DatePickerItemData[]>(generateItems(ITEMS_PER_PAGE * 2, -ITEMS_PER_PAGE));
  const [firstItemIndex, setFirstItemIndex] = useState(VIRTUOSO_START_INDEX);

  const initialIndexOffset = useRef(INITIAL_INDEX);
  const [selectedIdx, setSelectedIdx] = useState<number | null>(null);

  const handleClick = useCallback(
    (item: DatePickerItemData) => {
      onChange(item.value);

      const firstItemIndex = initialIndexOffset.current || 0;
      let nextFirstItemIdx = firstItemIndex - INITIAL_INDEX - ITEMS_PER_PAGE;
      const missingTopItems = nextFirstItemIdx - item.idx + VERTICAL_COMPENSATION;

      // prepend date items, when the selected date's index is too low we prepend
      // some date options so that it will remain vertically centered in the middle
      if (missingTopItems >= 0) {
        nextFirstItemIdx -= missingTopItems;
        initialIndexOffset.current -= firstItemIndex - missingTopItems;
        setItems((items) => [...generateItems(missingTopItems, nextFirstItemIdx), ...items]);
      }
      setSelectedIdx(item.idx);
    },
    [setItems, generateItems, initialIndexOffset, onChange],
  );

  const appendItems = useCallback(
    (lastItemIndex: number) => {
      setItems((items) => [...items, ...generateItems(ITEMS_PER_PAGE, lastItemIndex)]);
    },
    [setItems, generateItems],
  );

  // @see https://git.io/JIUuo
  const prependItems = useCallback(() => {
    const firstItemIndex = initialIndexOffset.current || 0;
    const nextFirstItemIdx = firstItemIndex - INITIAL_INDEX - ITEMS_PER_PAGE;
    if (initialIndexOffset) {
      initialIndexOffset.current -= ITEMS_PER_PAGE;
    }
    setItems((items) => [...generateItems(ITEMS_PER_PAGE, nextFirstItemIdx), ...items]);
    setFirstItemIndex(firstItemIndex - ITEMS_PER_PAGE);
    return false;
  }, [setItems, generateItems, initialIndexOffset]);

  // on mount check if there is a selected value and save its idx in state
  useEffect(() => {
    if (value) {
      let initialSelectedIdx = null;
      for (let i = 0; i < items.length; i++) {
        if (items[i].isSelected(value)) {
          initialSelectedIdx = items[i].idx;
          break;
        }
      }
      setSelectedIdx(initialSelectedIdx);
    }
  }, []); // eslint-disable-line

  return (
    <Virtuoso
      data={items}
      firstItemIndex={firstItemIndex}
      itemContent={(_, item) => (
        <div
          className={
            itemClassName + ` mml-datepicker__item ${item.idx === selectedIdx ? 'mml-datepicker__item--selected' : ''}`
          }
          onClick={() => handleClick(item)}
        >
          {item.displayValue}
        </div>
      )}
      endReached={appendItems}
      startReached={prependItems}
    />
  );
};
import React, { FC } from 'react';
import dayjs, { Dayjs } from 'dayjs';

import { DatePickerProps } from './DatePicker';
import { DatePickerSelect, DatePickerSelectReadyProps, DatePickerSelectProps } from './DatePickerSelect';

export type DatePickerDateProps = DatePickerSelectReadyProps & {
  format: NonNullable<DatePickerProps['dateFormat']>;
  interval: NonNullable<DatePickerProps['dateInterval']>;
};

/**
 * Get item data
 */
const getItemData: DatePickerSelectProps['getItemData'] = (props) => {
  const { idx, interval, value, format } = props;
  const newValue =
    idx >= 0
      ? dayjs(value).add(idx * (interval * 24), 'hour')
      : dayjs(value).subtract(idx * (interval * 24) * -1, 'hour');

  return {
    idx,
    value: newValue,
    displayValue: dayjs(newValue).format(format),
    isSelected: (currentValue: Dayjs) =>
      dayjs(newValue).isSame(interval === 1 ? dayjs(currentValue) : dayjs(currentValue).add(interval, 'day'), 'date'),
  };
};

/**
 * DatePicker date
 */
export const DatePickerDate: FC<DatePickerDateProps> = (props) => (
  <div className="mml-datepicker__select mml-datepicker__date">
    <DatePickerSelect {...props} itemClassName="mml-datepicker__item--day" getItemData={getItemData} />
  </div>
);
import React, { FC } from 'react';
import dayjs, { Dayjs } from 'dayjs';
import isBetween from 'dayjs/plugin/isBetween';

import { DatePickerProps } from './DatePicker';
import { DatePickerSelect, DatePickerSelectProps, DatePickerSelectReadyProps } from './DatePickerSelect';

dayjs.extend(isBetween);

export type DatePickerTimeProps = DatePickerSelectReadyProps & {
  format: NonNullable<DatePickerProps['timeFormat']>;
  interval: NonNullable<DatePickerProps['timeInterval']>;
};

/**
 * Get item data
 */
const getItemData: DatePickerSelectProps['getItemData'] = (props) => {
  const { idx, interval, value, format } = props;
  const newValue =
    idx >= 0 ? dayjs(value).add(idx * interval, 'minute') : dayjs(value).subtract(idx * -1 * interval, 'minute');

  return {
    idx,
    value: newValue,
    displayValue: dayjs(newValue).format(format),
    isSelected: (currentValue: Dayjs) =>
      dayjs(newValue).isBetween(currentValue, dayjs(currentValue).add(interval - 1, 'minute'), 'minute', '[]'),
  };
};

/**
 * DatePicker time
 */
export const DatePickerTime: FC<DatePickerTimeProps> = (props) => (
  <div className="mml-datepicker__select mml-datepicker__time">
    <DatePickerSelect {...props} itemClassName="mml-datepicker__item--time" getItemData={getItemData} />
  </div>
);
import React, { useState, FC } from 'react';
import { Dayjs } from 'dayjs';

import { DatePickerDate } from './DatePickerDate';
import { DatePickerTime } from './DatePickerTime';

export type DatePickerProps = {
  /** The scheduler name attached to hidden input */
  name: string;
  /** The selected date time */
  selected: Dayjs;
  /** Interval in days for time selection */
  dateInterval: number;
  /**  Interval in minutes for time selection */
  timeInterval: number;
  /** Show only the date picker(without time picker) */
  fullDay: boolean;
  /** Date format, see [dayjs docs](https://day.js.org/docs/en/display/format) */
  dateFormat: string;
  /** Time format, see [dayjs docs](https://day.js.org/docs/en/display/format) */
  timeFormat: string;
  /** Filter dates, it should return a boolean */
  icalFilter: (date: Dayjs) => boolean;
};

export const DatePicker: FC<DatePickerProps> = ({
  name,
  selected,
  dateInterval,
  timeInterval,
  dateFormat,
  timeFormat,
  fullDay,
  icalFilter,
}) => {
  const [date, setDate] = useState(selected);

  const handleChangeDate = (value: Dayjs) => {
    setDate((prevDate) =>
      prevDate
        .set('date', value.get('date'))
        .set('month', value.get('month'))
        .set('year', value.get('year')),
    );
  };

  const handleChangeTime = (value: Dayjs) => {
    setDate((prevDate) => prevDate.set('minute', value.get('minute')).set('hour', value.get('hour')));
  };

  return (
    <div className={`mml-datepicker mml-datepicker--${fullDay ? 'single' : 'double'}`}>
      <input name={name} value={date.toISOString()} type="hidden" />

      <DatePickerDate
        icalFilter={icalFilter}
        format={dateFormat}
        value={date}
        onChange={handleChangeDate}
        interval={dateInterval}
      />

      {!fullDay && (
        <DatePickerTime
          icalFilter={icalFilter}
          format={timeFormat}
          value={date}
          onChange={handleChangeTime}
          interval={timeInterval}
        />
      )}
    </div>
  );
};
import React, { useState, useEffect, FC } from 'react';
// @ts-ignore
import IcalExpander from 'ical-expander';
import dayjs, { Dayjs } from 'dayjs';

import { DatePicker } from './DatePicker';
import { Card } from './Card';
import { CardHeader } from './CardHeader';
import { CardBody } from './CardBody';
import { Error as ErrorComponent } from './Error';
import { Loading as LoadingComponent } from './Loading';

export type SchedulerProps = {
  /**
   * The scheduler name attached to hidden input
   */
  name: string;
  /**
   * The selected date, it must be a valid parseable date string
   */
  selected?: string;
  /**
   * Interval in days for day selection
   * @default 1
   */
  dateInterval?: number;
  /**
   * Interval in minutes for time selection
   * @default 30
   */
  timeInterval?: number;
  /**
   * Date format, see [dayjs docs](https://day.js.org/docs/en/display/format)
   * @default 'ddd MMM DD'
   */
  dateFormat?: string;
  /**
   * Time format, see [dayjs docs](https://day.js.org/docs/en/display/format)
   * @default 'hh:mm A'
   */
  timeFormat?: string;
  /**
   * The duration of the event in minutes, used to check availability with ical
   * @default 30
   */
  duration?: number;
  /**
   * ICal availability public URL
   */
  icalAvailability?: string;
  /**
   * Show only the date picker(without time picker) if events lasts a whole day
   * @default false
   */
  fullDay?: boolean;
};

export type ICalFilter = (start?: Dayjs) => boolean;

const setupIcalFilter = async (icalURL: string, duration: number) => {
  const response = await fetch(icalURL, { method: 'GET', redirect: 'follow', credentials: 'same-origin' });
  const body = await response.text();
  if (!response.ok) throw new Error(body);

  const icalExpander = new IcalExpander({ ics: body, maxIterations: 10 });

  return () => (start?: Dayjs) => {
    if (!start) return true;
    const { events } = icalExpander.between(start.toDate(), start.add(duration, 'minute').toDate());
    return !events.length;
  };
};

export const Scheduler: FC<SchedulerProps> = ({
  name,
  selected,
  icalAvailability,
  duration = 30,
  dateInterval = 1,
  timeInterval = 30,
  dateFormat = 'ddd MMM DD',
  timeFormat = 'hh:mm A',
  fullDay = false,
}) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [icalFilter, setIcalFilter] = useState<ICalFilter>(() => () => true);

  useEffect(() => {
    if (!icalAvailability) return;

    setLoading(true);
    setupIcalFilter(icalAvailability, duration)
      .then(setIcalFilter)
      .catch((err) => {
        console.warn('loading ical failed', { icalAvailability, err });
        setError('iCal availability could not be loaded');
      })
      .finally(() => setLoading(false));
  }, [icalAvailability, duration]);

  return (
    <Card className="mml-scheduler">
      <CardHeader icon="date_range" text="Scheduler" />
      <CardBody>
        {error && !loading && <ErrorComponent error={`Failed, error: ${error}`} />}
        {!error && loading && <LoadingComponent loading={true} text="Loading availability" />}
        {!error && !loading && (
          <DatePicker
            name={name}
            selected={selected ? dayjs(selected) : dayjs().startOf('hour')}
            dateInterval={dateInterval}
            timeInterval={timeInterval}
            dateFormat={dateFormat}
            timeFormat={timeFormat}
            fullDay={fullDay}
            icalFilter={icalFilter}
          />
        )}
      </CardBody>
    </Card>
  );
};
