import { ReactElement } from 'react';
import { XmlElement } from '@rgrove/parse-xml';

import { MMLTag } from './MMLTag';
import { converters as defaultConverters, ConvertorType } from './converters';

/** The type attribute of <mml> determine how its inner content is visually displayed */
export type RootType = 'card';

/**
 * Tree - The tree object for MML tags
 */
export class Tree {
  converters: Record<string, ConvertorType>;
  node: XmlElement;
  children: MMLTag[];
  reactElements: ReactElement[];
  name?: string;
  type?: RootType;

  constructor(node: XmlElement, children: MMLTag[], customConvertors?: Record<string, ConvertorType>) {
    this.converters = { ...defaultConverters, ...customConvertors };
    this.node = node;
    this.children = children;
    this.reactElements = this.toReact();

    this.name = node.attributes.name;
    this.type = node.attributes.type as RootType;
  }

  /**
   * convert all nodes to react and maintain the hierarchy
   */
  toReact(parent: Tree | MMLTag = this) {
    const reactNodes: ReactElement[] = [];

    (parent.children || []).forEach((child, i) => {
      const converter = this.converters[child.name];
      if (!converter || !Object.hasOwnProperty.call(this.converters, child.name)) {
        throw Error(
          `Converter not found for tag ${child.name}, Available converters are ${Object.keys(this.converters)}`,
        );
      }

      const children = this.toReact(child);
      child.key = `tag-${child.name}-position-${i}`;
      reactNodes.push(converter(child, children));
    });

    return reactNodes;
  }
}
import parseXml from '@rgrove/parse-xml';

import { ConvertorType } from './converters';
import { MMLTag } from './MMLTag';
import { Tree } from './tree';

/**
 * SourceToXML - Takes an MML string and converts it to XML nodes
 *
 * @param {string} source MML tag string
 *
 * @returns {array} an Array of XML nodes
 */
export function SourceToXML(source: string) {
  let src = source.trim();
  // the wrapping MML tags are optional, for parsing simplicity we automatically add them if they are not already there
  if (!src.startsWith('<mml')) src = `<mml>${source}</mml>`;

  // emulate HTML handling of & escaping
  const unescapedAmps = /&(?!amp;|lt;|gt;)/g;
  src = src.replace(unescapedAmps, '&amp;');

  // convert the string to XML nodes
  // this library is relatively lightweight and doesn't do a ton of validation
  return parseXml(src);
}

function convertNodes(nodes: parseXml.XmlNode[]) {
  return nodes.reduce((acc: MMLTag[], node: parseXml.XmlNode) => {
    const element = node as parseXml.XmlElement;
    let children;
    if (element.children) children = convertNodes(element.children);

    let { name } = element;
    if ((node as parseXml.XmlText).type === 'text') {
      if ((node as parseXml.XmlText).text.trim().length) name = 'text';
      else return acc; // skip empty text elements
    }

    acc.push(new MMLTag(name, node as parseXml.XmlElement | parseXml.XmlText, children));
    return acc;
  }, []);
}

/**
 * XMLtoMMLTree - Takes an array of XML nodes and converts it into an MML Tree
 *
 * @param {type} XMLNodes an array of XML nodes
 *
 * @returns {MMLTree} The MML tree
 */
export function XMLtoMMLTree(document: parseXml.XmlDocument, customConvertors?: Record<string, ConvertorType>) {
  if (!document || !document.children || !document.children.length) throw new Error('bad input');

  const mmlNode = document.children[0] as parseXml.XmlElement;
  if (mmlNode.name !== 'mml') throw new Error('missing mml tag');

  return new Tree(mmlNode, convertNodes(mmlNode.children), customConvertors);
}

/**
 * Takes an MML string and returns an MML Tree
 *
 * @param {string} source MML tag string
 *
 *  @returns {Tree} An MML Tree
 */
export function Parse(source: string, customConvertors?: Record<string, ConvertorType>) {
  const XMLNodes = SourceToXML(source);
  return XMLtoMMLTree(XMLNodes, customConvertors);
}
import React, { useState, useMemo, useCallback, FC, ComponentType, FormEvent } from 'react';

import { Parse, ConvertorType } from '../parser';
import { Loading as LoadingComponent, LoadingProps } from '../components/Loading';
import { Error as ErrorComponent, ErrorProps } from '../components/Error';
import { Success as SuccessComponent, SuccessProps } from '../components/Success';

export type MMLProps = {
  /** The MML string source to render */
  source: string;
  /** The convert config allows you to overwrite the MML to react conversion */
  converters?: Record<string, ConvertorType>;
  /** The submit callback whenever a form is submitted, submit is expected to return a promise */
  onSubmit?: (data: Record<string, any>) => Promise<any> | any;
  /** Select a theme from built in available themes, it will be added as a className to the container(root) div */
  theme?:
    | 'base-dark'
    | 'messaging-light'
    | 'messaging-dark'
    | 'team-light'
    | 'team-dark'
    | 'commerce-light'
    | 'commerce-dark'
    | 'livestream-light'
    | 'livestream-dark'
    | string;
  /** Custom classname, appended to wrapper classname */
  className?: string;
  /** The Loading component, accepts null to render nothing */
  Loading?: ComponentType<LoadingProps> | null;
  /** The error component, accepts null to render nothing */
  Error?: ComponentType<ErrorProps> | null;
  /** The success message component, accepts null to render nothing */
  Success?: ComponentType<SuccessProps> | null;
};

/**
 * MML root component
 */
export const MML: FC<MMLProps> = ({
  source,
  onSubmit,
  converters,
  theme = '',
  className = '',
  Loading = LoadingComponent,
  Error = ErrorComponent,
  Success = SuccessComponent,
}) => {
  const [error, setError] = useState('');
  const [submitState, setSubmitState] = useState({ loading: false, error: '', success: '' });
  const tree = useMemo(() => {
    try {
      return Parse(source, converters);
    } catch (e) {
      console.warn('mml parsing error: ', source, e);
      setError("This chat message has invalid formatting and can't be shown");
      return null;
    }
  }, [source, converters]);

  const handleSubmit = useCallback(
    async (event: FormEvent<HTMLFormElement>) => {
      event.preventDefault();

      const state: Record<string, any> = {};
      if (tree?.name) state.mml_name = tree.name; // include mml_name in the data
      new FormData(event.currentTarget).forEach((value, key) => {
        state[key] = value;
      });

      if (!onSubmit) return console.warn('Forgot to pass onSubmit prop to <MML/>? payload:', state);

      try {
        setSubmitState({ loading: true, error: '', success: '' });
        await onSubmit(state);
        setSubmitState({ loading: false, error: '', success: 'submitted' });
      } catch (e) {
        setSubmitState({ loading: false, error: 'Failed to send the request. Please try again.', success: '' });
      }
    },
    [onSubmit, tree],
  );

  return (
    <div className={`mml-container ${theme} ${className}`} data-testid="mml-container">
      {error ? (
        <div className="mml-wrap">{Error && <Error error={error} />}</div>
      ) : (
        <form onSubmit={handleSubmit} className="mml-wrap" data-testid="mml-form">
          {tree?.type ? <div className="mml-card">{tree?.reactElements}</div> : tree?.reactElements}
          {submitState.loading && Loading && <Loading loading={submitState.loading} />}
          {submitState.success && Success && <Success success={submitState.success} />}
          {submitState.error && Error && <Error error={submitState.error} />}
        </form>
      )}
    </div>
  );
};
