import React, { FunctionComponent } from 'react'
import { CircleProps } from './types'
import { View, StyleSheet } from 'react-native'

const Circle: FunctionComponent<CircleProps> = props => {
	const size = props.size
	const height = props.height || size
	const width = props.width || size
	const borderWidth = props.borderWidth || 0
	const borderRadius = props.borderRadius || size / 2

	const innerSize = size - borderWidth * 2
	const innerBorderRadius = innerSize / 2
	return (
		<View
			style={[
				styles.container,
				{ height, width, borderRadius, backgroundColor: props.borderColor }
			]}
		>
			<View
				style={[
					styles.wrapper,
					{
						height: innerSize,
						width: innerSize,
						borderRadius: innerBorderRadius
					}
				]}
			>
				{props.children}
			</View>
		</View>
	)
}

export default Circle

const styles = StyleSheet.create({
	container: {
		justifyContent: 'center',
		alignItems: 'center'
	},
	wrapper: {
		overflow: 'hidden'
	}
})
