import React from 'react'
import { Text, StyleSheet, View } from 'react-native'
import { InitialsProps } from '../../types'

export default React.memo((props: InitialsProps) => {
	const borderRadius = props.size / 2
	const fontSize = props.size / 2.5
	return (
		<View
			style={[
				styles.container,
				props.containerStyle ? props.containerStyle : {},
				props.backgroundColor ? { backgroundColor: props.backgroundColor } : {},
				{ borderRadius, height: props.size, width: props.size }
			]}
		>
			<Text
				style={[
					styles.text,
					{ fontSize },
					props.textStyle ? props.textStyle : {},
					props.textColor ? { color: props.textColor } : {}
				]}
			>
				{props.initials.toUpperCase()}
			</Text>
		</View>
	)
})

const styles = StyleSheet.create({
	container: {
		backgroundColor: 'lightblue',
		alignItems: 'center',
		justifyContent: 'center'
	},
	text: {
		color: 'white',
		fontWeight: 'bold'
	}
})
