import React from 'react'
import { Text, StyleSheet } from 'react-native'
import moment from 'moment'
import { DateProps } from '../../types'

export default (props: DateProps) => {
	const text = props.dateToStringFunc
		? props.dateToStringFunc(props.date)
		: moment(props.date).calendar(undefined, {
				lastDay: '[Yesterday]',
				sameDay: 'LT',
				nextDay: '[Tomorrow at] LT',
				lastWeek: 'dddd',
				nextWeek: '[Next] dddd',
				sameElse: 'L'
		  })

	return <Text style={[styles.text, props.style]}>{text}</Text>
}

const styles = StyleSheet.create({
	text: {
		color: 'gray',
		paddingLeft: 5
	}
})
