import React from 'react'
import { View, Text, StyleSheet, Image } from 'react-native'
import { RoomCreatorHeaderProps } from '../types'
import Animated from 'react-native-reanimated'

export default (props: RoomCreatorHeaderProps) => {
	return (
		<Animated.View
			style={[
				styles.container,
				{ backgroundColor: props.backgroundColor },
				props.containerStyle
			]}
		>
			<View style={[styles.notchContainer]}>
				<View style={styles.notch} />
			</View>
			<View style={styles.wrapper}>
				{!!props.image && (
					<View style={[styles.imageContainer, props.imageStyle]}>
						<Image style={[styles.image]} source={{ uri: props.image }} />
					</View>
				)}
				<View>
					{!!props.title && (
						<Text style={[styles.text, props.titleStyle]}>{props.title}</Text>
					)}
					{!!props.subtitle && (
						<Text style={[props.subtitleStyle]}>{props.subtitle}</Text>
					)}
				</View>
			</View>
		</Animated.View>
	)
}

const styles = StyleSheet.create({
	container: {
		// height: 75,
		borderTopRightRadius: 10,
		borderTopLeftRadius: 10,
		overflow: 'hidden',
		backgroundColor: 'white',
		borderBottomColor: 'lightgray',
		borderBottomWidth: 1,
		padding: 10,
		zIndex: -1
	},
	wrapper: {
		flexDirection: 'row'
	},
	text: {
		fontWeight: 'bold',
		fontSize: 18
	},
	notchContainer: {
		alignItems: 'center',
		paddingVertical: 10
	},
	notch: {
		width: 40,
		height: 5,
		borderRadius: 5,
		backgroundColor: 'lightgray'
	},
	image: {
		...StyleSheet.absoluteFillObject
	},
	imageContainer: {
		height: 80,
		width: 80,
		marginRight: 10
	}
})
