export const getInitials = function(name: string | null, maxLength = 2) {
	const parts = name?.split(' ') ?? ''
	let initials = ''
	for (let i = 0; i < parts.length; i++) {
		if (parts[i].length > 0 && parts[i] !== '') {
			initials += parts[i][0]
		}
	}
	if (maxLength) initials = initials.slice(0, maxLength)
	return initials
}
